INSERT INTO `yxelia_options` VALUES("126", "_transient_dash_4077549d03da2e451c8b5f002294ff51", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/05/wordpress-4-2-2/\'>WordPress 4.2.2 Security and Maintenance Release</a> <span class=\"rss-date\">May 7, 2015</span><div class=\"rssSummary\">WordPress 4.2.2 is now available. This is a critical security release for all previous versions and we strongly encourage you to update your sites immediately. Version 4.2.2 addresses two security issues: The Genericons icon font package, which is used in a number of popular themes and plugins, contained an HTML file vulnerable to a cross-site [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/05/how-to-get-yourself-to-do-things/\'>Matt: How to Get Yourself to Do Things</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/a-wordpress-veterans-take-on-drupalcon-la\'>WPTavern: A WordPress Veteran’s Take on DrupalCon LA</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/community-translation-and-wapuu-how-japan-is-shaping-wordpress-history\'>WPTavern: Community, Translation, and Wapuu: How Japan is Shaping WordPress History</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/so-widgets-bundle/\' class=\'dashboard-news-plugin-link\'>SiteOrigin Widgets Bundle</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=so-widgets-bundle&amp;_wpnonce=a86bf4c14d&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'SiteOrigin Widgets Bundle\'>Install</a>)</span></li></ul></div>", "no");
